var timer = setInterval(()=>{
	$('.videowall-endscreen.ytp-show-tiles').each(() => {
		clearInterval(timer);

		const params = new URLSearchParams(location.search);
		const vId = params.get('v');

		var playlistId = null;
		// nếu không phải là Loading HVC, cày em nó ngay
		if(PLAYLIST.indexOf(vId)===-1) {
